namespace Magestore\Giftvoucher\Controller\Adminhtml\Giftvoucher;

/**
 * Adminhtml Giftvoucher HistoryGrid Action
 *
 * @category Magestore
 * @package  Magestore_Giftvoucher
 * @module   Giftvoucher
 * @author   Magestore Developer
 */
class Historygrid extends \Magento\Backend\App\Action
{
    public function execute()
    {
        return $this->getResponse()->setBody(
            $this->_view->getLayout()->createBlock('Magestore\Giftvoucher\Block\Adminhtml\Giftvoucher\Edit\Tab\History')
                ->setGiftvoucher($this->getRequest()->getParam('id'))->toHtml()
        );
    }
    
    protected function _isAllowed()
    {
        return $this->_authorization->isAllowed('Magestore_Giftvoucher::giftvoucher');
    }
}
